// Import necessary dependencies
import React, {useEffect, useState} from 'react'; 
import axios from 'axios'; 
import { useLocation, useParams } from 'react-router-dom';

// PlayerStats component
function PlayerStats(){


    // Get location and player data from router
    const location = useLocation(); 
    const {player} = location.state || {};
    const { playerId } = useParams();

    // State for player stats and Hall of Fame probability
    const [stats, setStats] = useState(null);
    const [probability, setProbability] = useState(null);

    // Fetch player stats when component mounts or playerId changes
    useEffect(() => {
        const fetchPlayerStats = async () => {
            try{
                // Make API call to get player stats
                const response = await axios.get(`http://localhost:3001/player-stats/${playerId}`);
                setStats(response.data.modelInput); 
                setProbability(response.data.probability); 
            } catch(error){
                console.error('Error fetching player stats:', error);
            }
        };

        if(playerId){
          fetchPlayerStats();
        }
    }, [playerId]); 

    // Render player stats
    return (
      <div className="player-stats-container">
        {stats ? (
          <div className="player-stats">
            <h1 className="player-name">{player.name} Stats</h1>
            <div className="stats-grid">
              <div className="stat-item">
                <span className="stat-label">Points Per Game:</span>
                <span className="stat-value">{stats.PPG.toFixed(2)}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Assists Per Game:</span>
                <span className="stat-value">{stats.APG.toFixed(2)}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Rebounds Per Game:</span>
                <span className="stat-value">{stats.RPG.toFixed(2)}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">MVPs:</span>
                <span className="stat-value">{stats.MVP}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">FMVPs:</span>
                <span className="stat-value">{stats.FMVP}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">DPOY:</span>
                <span className="stat-value">{stats.DPOY}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">ROY:</span>
                <span className="stat-value">{stats.ROY}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Championships:</span>
                <span className="stat-value">{stats.Championships}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">All Stars:</span>
                <span className="stat-value">{stats.AllStars}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">First Team All-NBA:</span>
                <span className="stat-value">{stats.FirstTeam}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Second Team All-NBA:</span>
                <span className="stat-value">{stats.SecondTeam}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Third Team All-NBA:</span>
                <span className="stat-value">{stats.ThirdTeam}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">First Team All-Defense:</span>
                <span className="stat-value">{stats.DefFirstTeam}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">First Team All-Rookie:</span>
                <span className="stat-value">{stats.RookieFirstTeam}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Second Team All-Rookie:</span>
                <span className="stat-value">{stats.RookieSecondTeam}</span>
              </div>
              </div>
          <div className="hof-probability">
            <h2>Hall of Fame Probability</h2>
            <div className="probability-bar">
              <div 
                className="probability-fill" 
                style={{ width: `${probability * 100}%` }}
              ></div>
            </div>
            <span className="probability-value">{(probability * 100).toFixed(2)}%</span>
          </div>
        </div>
      ) : (
        <p className="loading">Loading stats...</p>
      )}
    </div>
  );
}
export default PlayerStats;
