import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

// API configuration
const API_KEY = 'f0cd23a225mshffaa1235925387bp101584jsn2744ef5e9285';
const host = 'api-nba-v1.p.rapidapi.com';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
  background-color: #0a192f;
  color: #ffffff;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-top: 2rem;
`;

const SearchContainer = styled.div`
  position: relative;
  width: 50%;
  max-width: 600px;
  margin-top: 2rem;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 1rem;
  font-size: 1rem;
  border: none;
  border-radius: 24px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  outline: none;
`;

const PlayerList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
  width: 100%;
  max-height: 300px;
  overflow-y: auto;
  background-color: #ffffff;
  border-radius: 0 0 24px 24px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const PlayerItem = styled.li`
  padding: 1rem;
  cursor: pointer;
  color: #0a192f;

  &:hover {
    background-color: #f0f0f0;
  }
`;

function PlayerSearch() {
  const [players, setPlayers] = useState([]);
  const [query, setQuery] = useState('');
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPlayers = async () => {
      if (query.length >= 3) {
        try {
          const response = await axios.get('https://api-nba-v1.p.rapidapi.com/players', {
            headers: {
              'x-rapidapi-key': API_KEY,
              'x-rapidapi-host': host,
              'Content-Type':'application/json'
            },
            params: {
              search: query
            }
          });
          const transformedPlayers = response.data.response.map(player => ({
            id: player.id,
            name: `${player.firstname} ${player.lastname}`
          }));

          setPlayers(transformedPlayers);

        } catch (error) {
          console.error('Error fetching player data:', error);
        }
      } else {
        setPlayers([]);
      }
    };

    const timeoutId = setTimeout(() => {
      fetchPlayers();
    }, 300);

    return () => {
      clearTimeout(timeoutId);
    };
  }, [query]);

  useEffect(() => {
    setFilteredPlayers(
      players.filter(player => player.name.toLowerCase().includes(query.toLowerCase()))
    );
  }, [query, players]);

  const handlePlayerClick = player => {
    navigate(`/player-stats/${encodeURIComponent(player.name)}`, { state: { player } });
  };

  return (
    <Container>
      <Title>NBA Player Search</Title>
      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="Enter player's name"
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
        {filteredPlayers.length > 0 && (
          <PlayerList>
            {filteredPlayers.map(player => (
              <PlayerItem key={player.id} onClick={() => handlePlayerClick(player)}>
                {player.name}
              </PlayerItem>
            ))}
          </PlayerList>
        )}
      </SearchContainer>
    </Container>
  );
}

export default PlayerSearch;
