import React, {useEffect, useState} from 'react'; 
import axios from 'axios'; 
import { useLocation, useParams } from 'react-router-dom';


function PlayerStats(){
    const location = useLocation(); 
    const {player} = location.state || {};
    const { playerId } = useParams();
    const [stats, setStats] = useState(null);
    const [probability, setProbability] = useState(null);


    useEffect(() => {
        const fetchPlayerStats = async () => {
            try{
                //ensure this api endpoint works
                const response = await axios.get(`http://localhost:3001/player-stats/${playerId}`);
                setStats(response.data.input); 
                setProbability(response.data.probability); 
            } catch(error){
                console.error('Error fetching player stats:', error);
            }
        };

        if(playerId){
          fetchPlayerStats();
        }
    }, [player.id]); 


    return(
        <div>
            {stats ? (
                <div>
                <h1>{player.name} Stats</h1>
                <p>Points: {input.points}</p>
                <p>Assists: {stats.assists}</p>
                <p>Rebounds: {stats.rebounds}</p>
                <p>Steals: {stats.steals}</p>
                <p>Blocks: {stats.blocks}</p>
                <h2>Hall of Fame Probability: {(probability * 100).toFixed(2)}%</h2>
              </div>
            ) : (
              <p>Loading stats...</p>
            )}
          </div>
        );
}

export default PlayerStats;
